import React from 'react';
import { PaginationConfig, TableProps, SorterResult, TableCurrentDataSource } from 'antd/es/table';
import './rc-table.less';
export interface ITableData<T> {
    list: T[];
    pagination?: PaginationConfig;
}
interface IProps<T> extends TableProps<T> {
    onSelectRow?: (rows: T[]) => void;
    data: ITableData<T>;
    selectedRows?: T[];
    onChange?: (pagination: PaginationConfig, filters: Record<keyof T, string[]>, sorter: SorterResult<T>, extra?: TableCurrentDataSource<T>) => void;
}
declare const RcTable: React.FC<IProps<any>>;
export default RcTable;
