/**
 * 首先，每一行紧跟前导空白的插入值为多行时，保持缩进。
 *
 * 然后，移除每一行的公共前导空白。
 *
 * @param literals 字面值
 * @param interpolations 插入值
 * @returns 返回结果
 * @example
 * ```ts
 * const text = 'hello\nworld'
 * dent`
 *   ${text}
 *     -.-
 * ` // => 'hello\nworld\n  -.-'
 * ```
 */
export declare function dent(literals: TemplateStringsArray, ...interpolations: any[]): string;
/**
 * 移除文本中每一行的公共前导空白。
 *
 * @param text 要操作的文本
 * @returns 返回操作后的文本
 * @example
 * ```ts
 * dedent(`
 *   hello
 *   world
 *     -.-
 * `) // => 'hello\nworld\n  -.-'
 * ```
 */
export declare function dent(text: string): string;
export default function dent(literals: string | TemplateStringsArray, ...interpolations: any[]): string;
