// 常用正则
var phoneRegexp = /^(\+?0?86\-?)?1[3-9]\d{9}$/;

/**
 * 检查`value`是否为手机号。
 *
 * @since 0.0.6
 *
 * @param value
 * @returns {boolean}
 *
 * @example
 *
 * isPhone('17710067607')
 * // => true
 *
 * isBoolean('11710067607')
 * // => false
 */

function isPhone(value) {
  return phoneRegexp.test(value);
}

export { isPhone };
