import moment from 'moment';
import { IData } from './calendar';
/**
 * 解析时间范围
 * @param range
 *
 * 某一年
 * range: '2017'
 *
 * 某个月
 * range: '2017-02'
 *
 * 某个区间 可以月、天为单位 不能超过一年
 * range: ['2017-01-02', '2017-02-23']
 * range: ['2017-01', '2017-02']
 */
export declare function getDateRange(range: string | string[]): moment.Moment[];
export declare function getVirtualDate(range: string | string[]): string[];
/**
 * 获取chart数据
 * @param range
 * @param data
 * @param weekStart
 */
export declare function getChartData(range: string | string[], data: IData, weekStart: 1 | 7): {
    data: any[];
    months: any[];
};
export declare function getMonths(startMonth: number, endMonth: number): string[];
