import React from 'react';
import { LegendProps } from 'bizcharts';
import { TPadding } from '@/global';
export interface IDataItem {
    x: any;
    [key: string]: number;
}
export interface ILineChartProps {
    className?: string;
    style?: React.CSSProperties;
    padding?: TPadding;
    height?: number;
    colors?: string[];
    data: IDataItem[];
    title?: string | React.ReactNode;
    titleMap?: {
        [key: string]: any;
    };
    legend?: LegendProps;
    borderWidth?: number;
    legendPosition?: string;
    smooth: boolean;
}
declare const LineChart: React.FC<ILineChartProps>;
export default LineChart;
